import React from 'react';
import './NodeStatusCard.css';

function NodeStatusCard({ node, status, isSelected, onSelect }) {
  const isHealthy = status?.status === 'healthy';

  return (
    <div 
      className={`node-status-card ${isSelected ? 'selected' : ''} ${isHealthy ? 'healthy' : 'unhealthy'}`}
      onClick={onSelect}
    >
      <h3>{node.id}</h3>
      <div className="status-indicator">
        <span className="status-dot"></span>
        <span>{isHealthy ? 'Healthy' : 'Unhealthy'}</span>
      </div>

      {status && !status.error && (
        <div className="status-details">
          <p>Uptime: {status.uptime}</p>
          <p>Keys: {status.key_count}</p>
          <p>Port: {node.port}</p>
        </div>
      )}

      {status?.error && (
        <div className="error-message">
          {status.error}
        </div>
      )}
    </div>
  );
}

export default NodeStatusCard;
