import React, { useState } from 'react';
import './KeyValueForm.css';

function KeyValueForm({ selectedNode }) {
  const [key, setKey] = useState('');
  const [value, setValue] = useState('');
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async (action) => {
    setLoading(true);
    setResult(null);

    try {
      let response;
      const url = `http://localhost:${selectedNode.port}/store/${key}`;

      switch (action) {
        case 'get':
          response = await fetch(url);
          break;
        case 'put':
          response = await fetch(url, {
            method: 'PUT',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({ value })
          });
          break;
        case 'delete':
          response = await fetch(url, { method: 'DELETE' });
          break;
        default:
          throw new Error('Invalid action');
      }

      const data = await response.json();
      setResult({
        success: response.ok,
        data: data
      });
    } catch (error) {
      setResult({
        success: false,
        error: error.message
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="key-value-form">
      <h2>Key-Value Operations</h2>

      <div className="form-group">
        <label>Key:</label>
        <input
          type="text"
          value={key}
          onChange={(e) => setKey(e.target.value)}
          placeholder="Enter key"
        />
      </div>

      <div className="form-group">
        <label>Value:</label>
        <input
          type="text"
          value={value}
          onChange={(e) => setValue(e.target.value)}
          placeholder="Enter value"
        />
      </div>

      <div className="button-group">
        <button
          onClick={() => handleSubmit('get')}
          disabled={loading || !key}
        >
          Get
        </button>
        <button
          onClick={() => handleSubmit('put')}
          disabled={loading || !key || !value}
        >
          Put
        </button>
        <button
          onClick={() => handleSubmit('delete')}
          disabled={loading || !key}
        >
          Delete
        </button>
      </div>

      {loading && <div className="loading">Loading...</div>}

      {result && (
        <div className={`result ${result.success ? 'success' : 'error'}`}>
          {result.success ? (
            <pre>{JSON.stringify(result.data, null, 2)}</pre>
          ) : (
            <div className="error-message">{result.error}</div>
          )}
        </div>
      )}
    </div>
  );
}

export default KeyValueForm;
